// Rust: Data transformation core (compiled to WebAssembly Component)
use wasm_bindgen::prelude::*;

#[wasm_bindgen]
pub fn compress_signal(data: &[f32]) -> Vec<f32> {
    // Example of a compute-heavy DSP operation
    let filtered: Vec<f32> = data.iter().map(|x| (x * 0.85).sin()).collect();
    filtered
}

code_examples.go:
// Go: Capâ€™n Proto RPC service exposing Rust WASM module
package main

import (
  "context"
  capnp "capnproto.org/go/capnp/v3"
  wasm "github.com/kodr/runtime/wasm"
)

type DSPService struct{}

func (s *DSPService) Process(ctx context.Context, call capnp.DSPService_process) error {
  input, _ := call.Params.Input()
  data := parseFloats(input)
  // Call compiled Rust WASM function
  processed := wasm.Invoke[[]float32]("compress_signal", data)
  call.Results.SetOutput(toBytes(processed))
  return nil
}

code_examples.javascript:
// JS: Browser orchestrator running all modules together
import { DSPService, PythonPredictor } from './wasm'

async function analyze(audioBuffer) {
  const rawData = new Float32Array(audioBuffer)
  const processed = await DSPService.process(rawData)
  const prediction = await PythonPredictor.predict_enhanced_waveform(processed)
  console.log('AI Prediction:', prediction)
}

// Unified cross-language orchestration
await analyze(await fetch('/samples/waveform.raw').then(r => r.arrayBuffer()))
